package basics;

public class Marks {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int marks=50;
		if(marks>=90){
			System.out.println("A grade");
		}else if(marks>=80) {
			System.out.println("B grade");
		}else if(marks>=70) {
			System.out.println("C grade");
		}else {
			System.out.println("d grade");
		}
	}

}
